SELECT PRE.NUMEROPOLIZAINPUT, PN_A.DOCUMENTOIDENTIDADINPUT, PN_A.FECHANACIMIENTOINPUT,OA.VALORFECNACINPUT
FROM INTERSEGURO.AGREGATEDPOLICY APOL  
INNER JOIN INTERSEGURO.CONTEXTOPERATION CTX ON APOL.OPERATIONPK = CTX.ID AND  CTX.STATUS = 2       
INNER JOIN INTERSEGURO.POLICYDCO PDCO ON CTX.ID=PDCO.OPERATIONPK 
INNER JOIN INTERSEGURO.PREPOLICY PRE ON PDCO.DCOID=PRE.PK
INNER JOIN INTERSEGURO.PRODUCT PRO ON APOL.PRODUCTID=PRO.PRODUCTID  
INNER JOIN INTERSEGURO.PRODUCTPROPERTY PROPRO ON PROPRO.PRO_ID = PRO.PRODUCTID --AND PROPRO.PRP_TYPE = 2 
INNER JOIN INTERSEGURO.INSURANCEOBJECTDCO IDCO ON IDCO.OPERATIONPK = CTX.ID
INNER JOIN INTERSEGURO.OADESGHIPOTECARIOIND   OA ON OA.PK = IDCO.DCOID
INNER JOIN INTERSEGURO.STPO_INSOBJPARTICIPATION ST ON ST.AGREGATEDPARENTID = IDCO.AGREGATEDOBJECTID
INNER JOIN INTERSEGURO.STTE_THIRDPARTY TP_CONT ON TP_CONT.TPT_ID = ST.THIRDPARTYID  
INNER JOIN INTERSEGURO.PERSONANATURAL PN_A ON TP_CONT.IDDCO=PN_A.PK  
